import BootstrapTable from 'react-bootstrap-table-next';
import { Spinner } from "react-bootstrap";
import React from 'react';


function add_column(columns, dataField, text, sort){
  columns.push({
    dataField: dataField,
    text: text,
    sort: sort
  })
}

function create_data_arrays(data){
  let table_data = [];
  let row1 = [];
  let row2 = [];
  for (let key in data) {
    if (data.hasOwnProperty(key)) {
      row1.push(key);
      row2.push(data[key]);
    }
  }
  table_data.push(row1);
  table_data.push(row2);
  return table_data
}

function Table({data, columnTitles}) {
  if(!data || data.length === 0) return <Spinner animation="border" variant="primary" size="mg"/>;
  console.log("tableData", data);
  let columns = [];
  let table_data = [];
  columnTitles.forEach(element => add_column(columns, element, element, true));
  if (columnTitles.length === 2){
    table_data = create_data_arrays(data)
  }

  return <BootstrapTable keyField={columnTitles[0]}
                         data={ table_data }
                         columns={ columns }
                         striped
                         hover
                         condensed/>
}




export default Table;
