import useSWR from 'swr';

export const useRequest = (url) => {
  if (!url) {
    throw new Error('URL is required')
  }
  console.log("useRequest url", url);
  const { data, error } = useSWR(url, {refreshInterval:3000});
  console.log("useRequest data", data);
  return { data, error }
};
