import React, { Component } from "react";
import ChartistGraph from "react-chartist";
import { Container, Row, Col, Table, Spinner } from "react-bootstrap";
import { Card } from "components/Card/Card.jsx";
import { StatsCard } from "components/StatsCard/StatsCard.jsx";
import { Tasks } from "components/Tasks/Tasks.jsx";
import {
  dataPie,
  legendPie,
  dataSales,
  optionsSales,
  responsiveSales,
  legendSales,
  dataBar,
  optionsBar,
  responsiveBar,
  legendBar,
  horizontalBar,
  horizontalOptionsBar,
  statsEndpoint,
  thArrayPackageTable
} from "variables/Variables.jsx";

class Dashboard extends Component {
  constructor(props) {
    super(props);
    this.state = {
      number_of_apps: 0,
      androguard_report_count: 0,
      version_list: [],
      brand_list: [],
      model_list: [],
      number_of_firmware_samples: [],
      locale_list: [],
      manufacturer_list: [],
      packagename_list: [],
      isLoadingStatsComplete: false,
    };
  }

  fetch_stats_data(api_endpoint, ){
    fetch(api_endpoint)
      .then(response => {
        if (response.ok) {
          return response.json();
        } else {
          throw new Error('Something went wrong ...');
        }
      })
      .then(data => {
        console.log("Data received: " + JSON.stringify(data));
        this.setState({
          number_of_apps: data.number_of_apps,
          version_list: data.version_list,
          brand_list: data.brand_list,
          model_list: data.model_list,
          number_of_firmware_samples: data.number_of_firmware_samples,
          locale_list: data.locale_list,
          manufacturer_list: data.manufacturer_list,
          packagename_list: data.packagename_list,
          androguard_report_count: data.androguard_report_count,
          isLoadingStatsComplete: true,
        })
      })
      .catch(error => this.setState({ error, isLoadingStatsComplete: false }));
  }

  componentDidMount() {
    this.fetch_stats_data(statsEndpoint)

  }

  static createPercentageDict(data){
    console.log(data);
    let total_samples = 0;
    for (let [key, value] of Object.entries(data)) {
      total_samples = total_samples + value;
    }
    let percentage_dict = {};
    for (let [key, value] of Object.entries(data)) {
      percentage_dict[key] = total_samples/100 * value;
    }
    return percentage_dict
  };


  static createLegend(json) {
    let legend = [];
    for (let i = 0; i < json["names"].length; i++) {
      let type = "fa fa-circle text-" + json["types"][i];
      legend.push(<i className={type} key={i} />);
      legend.push(" ");
      legend.push(json["names"][i]);
    }
    return legend;
  };


  render() {
    const { number_of_apps,
      isLoadingStatsComplete,
      packagename_list,
      number_of_firmware_samples,
      androguard_report_count,
      version_list} = this.state;

    let version_pie_chart;
    if (isLoadingStatsComplete){
      version_pie_chart = <ChartistGraph data={Dashboard.createPercentageDict(version_list)}
                                             type="Pie" />
    }else{
      version_pie_chart = <Spinner animation="border" variant="primary" size="lg"/>
    }

    return (
      <div className="content">
        <Container fluid>
          <Row>
            <Col lg={3} sm={6}>
              <StatsCard
                bigIcon={<i className="pe-7s-server text-warning" />}
                statsText="Capacity"
                statsValue="XXXX"
                statsIcon={<i className="fa fa-refresh" />}
                statsIconText="Updated now"
              />
            </Col>
            <Col lg={3} sm={6}>
              <StatsCard
                bigIcon={<i className="pe-7s-wallet text-success" />}
                statsText="Firmware Samples"
                statsValue={number_of_firmware_samples}
                statsIcon={<i className="fa fa-calendar-o" />}
                statsIconText="Last day"
              />
            </Col>
            <Col lg={3} sm={6}>
              <StatsCard
                bigIcon={<i className="pe-7s-graph1 text-danger" />}
                statsText="App Samples"
                statsValue={number_of_apps}
                statsIcon={<i className="fa fa-clock-o" />}
                statsIconText="In the last hour"
              />
            </Col>
            <Col lg={3} sm={6}>
              <StatsCard
                bigIcon={<i className="pe-7s-graph1 text-danger" />}
                statsText="AndroGuard Reports"
                statsValue={androguard_report_count}
                statsIcon={<i className="fa fa-clock-o" />}
                statsIconText="In the last hour"
              />
            </Col>
          </Row>
          <Row>
            <Col md={4}>
              <Card
                title="Android Versions"
                category="Samples by Versions"
                content={
                  <div
                    id="chartPreferences"
                    className="ct-chart ct-perfect-fourth"
                  >
                    {version_pie_chart}
                  </div>
                }
                legend={
                  <div className="legend">{Dashboard.createLegend(legendPie)}</div>
                }
              />
            </Col>
            <Col md={4}>
              <Card
                title="Permissions usage"
                category="Most used permissions"
                content={
                  <div
                    id="chartPreferences"
                    className="ct-chart ct-perfect-fourth"
                  >
                    <ChartistGraph data={dataPie} type="Pie" />
                  </div>
                }
                legend={
                  <div className="legend">{Dashboard.createLegend(legendPie)}</div>
                }
              />
            </Col>
            <Col md={4}>
              <Card
                title="Certificate Issuer"
                category="Most issued certificates"
                content={
                  <div
                    id="chartPreferences"
                    className="ct-chart ct-perfect-fourth"
                  >
                    <ChartistGraph data={dataPie} type="Pie" />
                  </div>
                }
                legend={
                  <div className="legend">{Dashboard.createLegend(legendPie)}</div>
                }
              />
            </Col>
          </Row>

          <Row>
            <Col md={6}>
              <Card
                id="chartActivity"
                title="Firmware by Region"
                category="All firmware by locale"
                content={
                  <div className="ct-chart">
                    <ChartistGraph
                      data={dataBar}
                      type="Bar"
                      options={optionsBar}
                      responsiveOptions={responsiveBar}
                    />
                  </div>
                }
                legend={
                  <div className="legend">{Dashboard.createLegend(legendBar)}</div>
                }
              />
            </Col>

            <Col md={6}>
              <Card
                title="Firmware Manufacturer"
                category="Smartphone manufacturer"
                content={
                  <div className="ct-chart">
                    <ChartistGraph
                      data={horizontalBar}
                      type="Bar"
                      options={horizontalOptionsBar}
                    />
                  </div>
                }
              />
            </Col>
          </Row>
          <Row>
            <Col md={6}>
              <Card
                id="chartHorBar"
                title="Firmware Brand"
                category="All samples sorted by Brand"
                content={
                  <div className="ct-chart">
                    <ChartistGraph
                      data={horizontalBar}
                      type="Bar"
                      options={horizontalOptionsBar}
                    />
                  </div>
                }
                legend={
                  <div className="legend">{Dashboard.createLegend(legendBar)}</div>
                }
              />
            </Col>

            <Col md={6}>
              <Card
                id="chartHorBar1"
                title="Most installed Apps"
                category="Occurrence by package name"
                content={
                  <div className="ct-chart">
                    <ChartistGraph
                      data={horizontalBar}
                      type="Bar"
                      options={horizontalOptionsBar}
                    />
                  </div>
                }
                legend={
                  <div className="legend">{Dashboard.createLegend(legendBar)}</div>
                }
              />
            </Col>
          </Row>

          <Row>
            <Card
              plain
              title="Top Packagenames"
              category="Packagename occurance on firmware."
              ctTableFullWidth
              ctTableResponsive
              content={
                <Table hover>
                  <thead>
                  <tr>
                    {thArrayPackageTable.map((prop, key) => {
                      return <th key={key}>{prop}</th>;
                    })}
                  </tr>
                  </thead>
                  <tbody>
                  {packagename_list.map((prop, key) => {
                    return (
                      <tr key={key}>
                        {prop.map((prop, key) => {
                          return <td key={key}>{prop}</td>;
                        })}
                      </tr>
                    );
                  })}
                  </tbody>
                </Table>
              }
            />
          </Row>
        </Container>
      </div>
    );
  }
}

export default Dashboard;
