import React, { Component } from "react";
import ChartistGraph from "react-chartist";
import { Container, Row, Col } from "react-bootstrap";
import { Card } from "components/Card/Card.jsx";
import { StatsCard } from "components/StatsCard/StatsCard.jsx";
import { Tasks } from "components/Tasks/Tasks.jsx";
import {
  dataPie,
  legendPie,
  dataSales,
  optionsSales,
  responsiveSales,
  legendSales,
  dataBar,
  optionsBar,
  responsiveBar,
  legendBar,
  horizontalBar,
  horizontalOptionsBar
} from "variables/Variables.jsx";

class Dashboard extends Component {
  constructor(props) {
    super(props);
    this.state = {
      number_of_apps: 0,
      isLoadingAppCount: true
    };
  }

  get_number_of_apps(){
    fetch('http://127.0.0.1:5000/api/v1/firmware/app_count/')
      .then(response => {
        if (response.ok) {
          return response.json();
        } else {
          throw new Error('Something went wrong ...');
        }
      })
      .then(data => this.setState({ number_of_apps: data, isLoadingAppCount: false }))
      .catch(error => this.setState({ error, isLoadingAppCount: false }));
  };

  componentDidMount() {
    this.get_number_of_apps()
  }

  static createLegend(json) {
    let legend = [];
    for (let i = 0; i < json["names"].length; i++) {
      let type = "fa fa-circle text-" + json["types"][i];
      legend.push(<i className={type} key={i} />);
      legend.push(" ");
      legend.push(json["names"][i]);
    }
    return legend;
  };



  render() {
    return (
      <div className="content">
        <Container fluid>
          <Row>
            <Col lg={3} sm={6}>
              <StatsCard
                bigIcon={<i className="pe-7s-server text-warning" />}
                statsText="Capacity"
                statsValue="XXXX"
                statsIcon={<i className="fa fa-refresh" />}
                statsIconText="Updated now"
              />
            </Col>
            <Col lg={3} sm={6}>
              <StatsCard
                bigIcon={<i className="pe-7s-wallet text-success" />}
                statsText="Firmware Samples"
                statsValue="XXXX"
                statsIcon={<i className="fa fa-calendar-o" />}
                statsIconText="Last day"
              />
            </Col>
            <Col lg={3} sm={6}>
              <StatsCard
                bigIcon={<i className="pe-7s-graph1 text-danger" />}
                statsText="App Samples"
                statsValue={this.number_of_apps}
                statsIcon={<i className="fa fa-clock-o" />}
                statsIconText="In the last hour"
              />
            </Col>
            <Col lg={3} sm={6}>
              <StatsCard
                bigIcon={<i className="pe-7s-graph1 text-danger" />}
                statsText="Certificates"
                statsValue="XXX"
                statsIcon={<i className="fa fa-clock-o" />}
                statsIconText="In the last hour"
              />
            </Col>
          </Row>
          <Row>
            <Col md={4}>
              <Card
                title="Android Versions"
                category="Samples by Versions"
                content={
                  <div
                    id="chartPreferences"
                    className="ct-chart ct-perfect-fourth"
                  >
                    <ChartistGraph data={dataPie} type="Pie" />
                  </div>
                }
                legend={
                  <div className="legend">{Dashboard.createLegend(legendPie)}</div>
                }
              />
            </Col>
            <Col md={4}>
              <Card
                title="Permissions usage"
                category="Most used permissions"
                content={
                  <div
                    id="chartPreferences"
                    className="ct-chart ct-perfect-fourth"
                  >
                    <ChartistGraph data={dataPie} type="Pie" />
                  </div>
                }
                legend={
                  <div className="legend">{Dashboard.createLegend(legendPie)}</div>
                }
              />
            </Col>
            <Col md={4}>
              <Card
                title="Certificate Issuer"
                category="Most issued certificates"
                content={
                  <div
                    id="chartPreferences"
                    className="ct-chart ct-perfect-fourth"
                  >
                    <ChartistGraph data={dataPie} type="Pie" />
                  </div>
                }
                legend={
                  <div className="legend">{Dashboard.createLegend(legendPie)}</div>
                }
              />
            </Col>
          </Row>

          <Row>
            <Col md={6}>
              <Card
                id="chartActivity"
                title="Firmware by Region"
                category="All firmware by locale"
                content={
                  <div className="ct-chart">
                    <ChartistGraph
                      data={dataBar}
                      type="Bar"
                      options={optionsBar}
                      responsiveOptions={responsiveBar}
                    />
                  </div>
                }
                legend={
                  <div className="legend">{Dashboard.createLegend(legendBar)}</div>
                }
              />
            </Col>

            <Col md={6}>
              <Card
                title="Firmware Manufacturer"
                category="Smartphone manufacturer"
                content={
                  <div className="ct-chart">
                    <ChartistGraph
                      data={horizontalBar}
                      type="Bar"
                      options={horizontalOptionsBar}
                    />
                  </div>
                }
              />
            </Col>
          </Row>
          <Row>
            <Col md={6}>
              <Card
                id="chartHorBar"
                title="Firmware Brand"
                category="All samples sorted by Brand"
                content={
                  <div className="ct-chart">
                    <ChartistGraph
                      data={horizontalBar}
                      type="Bar"
                      options={horizontalOptionsBar}
                    />
                  </div>
                }
                legend={
                  <div className="legend">{Dashboard.createLegend(legendBar)}</div>
                }
              />
            </Col>

            <Col md={6}>
              <Card
                id="chartHorBar1"
                title="Most installed Apps"
                category="Occurrence by package name"
                content={
                  <div className="ct-chart">
                    <ChartistGraph
                      data={horizontalBar}
                      type="Bar"
                      options={horizontalOptionsBar}
                    />
                  </div>
                }
                legend={
                  <div className="legend">{Dashboard.createLegend(legendBar)}</div>
                }
              />
            </Col>
          </Row>
        </Container>
      </div>
    );
  }
}

export default Dashboard;
