import React, { Component } from "react";
import ChartistGraph from "react-chartist";
import { Container, Row, Col, Table, Spinner } from "react-bootstrap";
import { Card } from "components/Card/Card.jsx";
import { StatsCard } from "components/StatsCard/StatsCard.jsx";
import { Tasks } from "components/Tasks/Tasks.jsx";
import {
  dataPie,
  legendPie,
  dataSales,
  optionsSales,
  responsiveSales,
  legendSales,
  dataBar,
  optionsBar,
  responsiveBar,
  legendBar,
  horizontalBar,
  horizontalOptionsBar,
  statsEndpoint,
  thArrayPackageTable,
  dataPieTest,
  thArray,
  tdArray,
  root
} from "variables/Variables.jsx";
import PieChart from "../components/Diagrams/PieChart";

class Dashboard extends Component {
  constructor(props) {
    super(props);
    this.state = {
      number_of_firmware_by_android_version: [],
      number_of_firmware_by_brand: [],
      number_of_firmware_by_model: [],
      number_of_firmware_by_locale: [],
      number_of_firmware_by_manufacturer: [],
      number_of_firmware_files: 0,
      number_of_firmware_by_region: [],
      number_of_apps_total: 0,
      packagename_count_dict: [],
      total_firmware_byte_size: 0,
      androguard_report_count: 0,
      isLoadingStatsComplete: false,
    };
  }

  componentDidMount() {

  }



  /**
   * Converts dict to table component data.
   */
  static createTableData(data){
    let table_data = [];
    for (let key in data) {
      if (data.hasOwnProperty(key)) {
        let row_data = [key, data[key]];
        table_data.push(row_data)
      }
    }
    return table_data;
  }


  static createLegend(json) {
    let legend = [];
    for (let i = 0; i < json["names"].length; i++) {
      let type = "fa fa-circle text-" + json["types"][i];
      legend.push(<i className={type} key={i} />);
      legend.push(" ");
      legend.push(json["names"][i]);
    }
    return legend;
  };


  render() {
    const {
      number_of_firmware_by_android_version,
      number_of_firmware_by_brand,
      number_of_firmware_by_model,
      number_of_firmware_by_locale,
      number_of_firmware_by_manufacturer,
      number_of_firmware_files,
      number_of_firmware_by_region,
      number_of_apps_total,
      packagename_count_dict,
      total_firmware_byte_size,
      androguard_report_count,
      isLoadingStatsComplete} = this.state;


    return (
      <div className="content">
        <Container fluid>
          <Row>
            <Col lg={3} sm={6}>
              <StatsCard
                bigIcon={<i className="pe-7s-server text-warning" />}
                statsText="Capacity"
                statsValue={total_firmware_byte_size}
                statsIcon={<i className="fa fa-refresh" />}
                statsIconText="Updated now"
              />
            </Col>
            <Col lg={3} sm={6}>
              <StatsCard
                bigIcon={<i className="pe-7s-wallet text-success" />}
                statsText="Firmware Samples"
                statsValue={number_of_firmware_files}
                statsIcon={<i className="fa fa-calendar-o" />}
                statsIconText="Last day"
              />
            </Col>
            <Col lg={3} sm={6}>
              <StatsCard
                bigIcon={<i className="pe-7s-graph1 text-danger" />}
                statsText="App Samples"
                statsValue={number_of_apps_total}
                statsIcon={<i className="fa fa-clock-o" />}
                statsIconText="In the last hour"
              />
            </Col>
            <Col lg={3} sm={6}>
              <StatsCard
                bigIcon={<i className="pe-7s-graph1 text-danger" />}
                statsText="AndroGuard Reports"
                statsValue={androguard_report_count}
                statsIcon={<i className="fa fa-clock-o" />}
                statsIconText="In the last hour"
              />
            </Col>
          </Row>

          <Row>
            <Col md={4}>
              <Card
                title="Android Version"
                category="Firmware clustered by version"
                content={
                  <div
                    id="chartPreferences"
                    className="ct-chart ct-perfect-fourth">

                  </div>
                }
              />
            </Col>
            <Col md={4}>

            </Col>
            <Col md={4}>

            </Col>
          </Row>


          <Row>
            <Col md={4}>
              <Card
                title="Android Versions"
                category="Firmware by Versions"
                content={
                  <div
                    id="chartPreferences"
                    className="ct-chart ct-perfect-fourth"
                  >
                    <PieChart dataurl={"http://127.0.0.1:5000/api/v1/statistics/"}/>
                  </div>
                }
                legend={
                  <div className="legend">{Dashboard.createLegend(legendPie)}</div>
                }
              />
            </Col>
            <Col md={4}>
              <Card
                title="Permissions usage"
                category="Most used permissions"
                content={
                  <div
                    id="chartPreferences"
                    className="ct-chart ct-perfect-fourth"
                  >
                    <ChartistGraph data={dataPie} type="Pie" />
                  </div>
                }
                legend={
                  <div className="legend">{Dashboard.createLegend(legendPie)}</div>
                }
              />
            </Col>
            <Col md={4}>
              <Card
                title="Certificate Issuer"
                category="Most issued certificates"
                content={
                  <div
                    id="chartPreferences"
                    className="ct-chart ct-perfect-fourth"
                  >
                    <ChartistGraph data={dataPie} type="Pie" />
                  </div>
                }
                legend={
                  <div className="legend">{Dashboard.createLegend(legendPie)}</div>
                }
              />
            </Col>
          </Row>

          <Row>
            <Col md={6}>
              <Card
                id="chartActivity"
                title="Firmware by Region"
                category="All firmware by locale"
                content={
                  <div className="ct-chart">
                    <ChartistGraph
                      data={dataBar}
                      type="Bar"
                      options={optionsBar}
                      responsiveOptions={responsiveBar}
                    />
                  </div>
                }
                legend={
                  <div className="legend">{Dashboard.createLegend(legendBar)}</div>
                }
              />
            </Col>

            <Col md={6}>
              <Card
                title="Firmware Manufacturer"
                category="Smartphone manufacturer"
                content={
                  <div className="ct-chart">
                    <ChartistGraph
                      data={horizontalBar}
                      type="Bar"
                      options={horizontalOptionsBar}
                    />
                  </div>
                }
              />
            </Col>
          </Row>
          <Row>
            <Col md={6}>
              <Card
                id="chartHorBar"
                title="Firmware Brand"
                category="All samples sorted by Brand"
                content={
                  <div className="ct-chart">
                    <ChartistGraph
                      data={horizontalBar}
                      type="Bar"
                      options={horizontalOptionsBar}
                    />
                  </div>
                }
                legend={
                  <div className="legend">{Dashboard.createLegend(legendBar)}</div>
                }
              />
            </Col>

            <Col md={6}>
              <Card
                id="chartHorBar1"
                title="Most installed Apps"
                category="Occurrence by package name"
                content={
                  <div className="ct-chart">
                    <ChartistGraph
                      data={horizontalBar}
                      type="Bar"
                      options={horizontalOptionsBar}
                    />
                  </div>
                }
                legend={
                  <div className="legend">{Dashboard.createLegend(legendBar)}</div>
                }
              />
            </Col>
          </Row>

          <Row>
            <Card
              plain
              title="Top Packagenames"
              category="Packagename occurance on firmware."
              ctTableFullWidth
              ctTableResponsive
              content={
                <Table hover>
                  <thead>
                  <tr>
                    {thArrayPackageTable.map((prop, key) => {
                      return <th key={key}>{prop}</th>;
                    })}
                  </tr>
                  </thead>
                  <tbody>
                  {Dashboard.createTableData(packagename_count_dict).map((prop, key) => {
                    return (
                      <tr key={key}>
                        {prop.map((prop, key) => {
                          return <td key={key}>{prop}</td>;
                        })}
                      </tr>
                    );
                  })}
                  </tbody>
                </Table>
              }
            />
          </Row>
        </Container>
      </div>
    );
  }
}

export default Dashboard;
