import React from 'react';
import useSWR from 'swr';
import ChartistGraph from "react-chartist";
import Legend from "chartist-plugin-legend";
import { Spinner } from "react-bootstrap";


/*
 * Creates a data object for ChartistGraph Pie chart.
 * @param: data: json
 */
function createPieChartData(data){
  let total_samples = 0;
  for (let key in data) {
    if (data.hasOwnProperty(key)) {
      total_samples = total_samples + data[key];
    }
  }
  let labels = [];
  let series = [];
  for (let key in data) {
    if (data.hasOwnProperty(key)) {
      labels.push(key);
      let value = (total_samples/100) * data[key];
      value = Math.round((value + Number.EPSILON) * 100) / 100;
      series.push(value);
    }
  }
  console.log("labels", JSON.stringify(labels));
  console.log("Series", JSON.stringify(series));
  return {labels: labels, series: series}
}


function interpolate_percentage(value, data){
  let sum = function(a, b) { return a + b };
  return Math.round(value / data.series.reduce(sum) * 100) + '%';
}



function PieChart({dataUrl}) {
  const { data, error } = useSWR(dataUrl);
  if (error) return <h1>Something went wrong!</h1>;
  if (!data) return <Spinner animation="border" variant="primary" size="mg"/>;
  console.log("PieData", data.number_of_firmware_by_android_version);
  //const pieData = createPieChartData(data.number_of_firmware_by_android_version);

  let plugins = [Legend()];
  return <ChartistGraph options={{plugins, labelInterpolationFnc: interpolate_percentage}}
                        data={data.number_of_firmware_by_android_version}
                        type="Pie"/>;
}

export default PieChart;
